mapper.update:
update clazz set name = #{name}, room = #{room}, begin_date = #{beginDate}, end_date = #{endDate}, master_id = #{masterId},
subject = #{subject},
update_time = NOW() where id = #{id}

mapper.select:
select * from clazz


        name = #{name}



            and end_date >= #{begin} and end_date <= #{end}



order by update_time desc
